import React, { Component } from "react";

class Searchbar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: false,
    };
  }

  handleDropdownVisibility = (state) => {
    this.setState({ visible: state === 0 ? false : true });
  };
  handleDropdownSearch = (info) => {
    console.log(info);
  };

  render() {
    const dropdownVisibleClass = "dropdown-content";
    const dropdownHiddenClass = "dropdown-content hidden";
    return (
      <div id="search" className="card">
        <div className="card-header justify-content-center ">
          Valitse haluamasi mittausasema havaintohistoriaa varten
        </div>
        <div className="card-body justify-content-center">
          <input
            type="text"
            placeholder="Hae..."
            id="dropdowninput"
            textContent=""
            onFocus={() => this.handleDropdownVisibility(1)}
            onBlur={() => this.handleDropdownVisibility(0)}
            onKeyUp={() => this.handleDropdownSearch(this.textContent)}
          />
          <div className="dropdown">
            <div
              id="dropdownbox"
              className={
                this.state.visible === true
                  ? dropdownVisibleClass
                  : dropdownHiddenClass
              }
            >
              <a href="">Valinta 1</a>
              <a href="">Valinta 2</a>
              <a href="">Valinta 3</a>
              <a href="">Valinta 4</a>
              <a href="">Valinta 5</a>
              <a href="">Valinta 6</a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Searchbar;
