import React, { Component } from "react";

class Searchbar extends Component {
  state = {
    visible: false,
  };

  handleDropdownVisibility = (state) => {
    this.state.visible = state === 0 ? "hidden" : "visible";
    console.log("Handlaa t채채", state);
  };
  render() {
    return (
      <div id="search" className="card">
        <div className="card-header justify-content-center ">
          Valitse haluamasi mittausasema havaintohistoriaa varten
        </div>
        <div className="card-body justify-content-center">
          <input
            type="text"
            placeholder="Hae..."
            id="dropdowninput"
            onKeyUp=""
            onFocus={() => this.handleDropdownVisibility(1)}
            outfocus={() => this.handleDropdownVisibility(0)}
          />
          <div className="dropdown">
            <div id="dropdownbox" className="dropdown-content">
              <a href="">Valinta 1</a>
              <a href="">Valinta 2</a>
              <a href="">Valinta 3</a>
              <a href="">Valinta 4</a>
              <a href="">Valinta 5</a>
              <a href="">Valinta 6</a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Searchbar;
