import React, { Component } from "react";

class Searchbar extends Component {
  state = {
    hidden: true,
  };
  handleSearchBarClick = () => {
    this.state.hidden = false;
    console.log("dropdown", this.state);
  };
  handleDropdown = () => {
    console.log("Hakukentt채채n kirjoitettu. Valmistele dropdown", this.props);
  };
  getClasses = () => {
    let classes = "";
    classes += this.state.hidden === true ? "dropdown hidden" : "dropdown";
    return classes;
  };
  render() {
    return (
      <div id="search" className="card">
        <div className="card-header d-flex justify-content-center ">
          Valitse haluamasi mittausasema havaintohistoriaa varten
        </div>
        <div className="card-body d-flex justify-content-center">
          <a id="a_form">
            <form className="form">
              <input
                className="form"
                type="text"
                placeholder="Hae asemaa"
                onClick={this.handleSearchBarClick}
                onKeyUp={this.handleDropdown}
              ></input>
            </form>
          </a>
          <div className={this.getClasses()} id="dropdown">
            <button>Asema 1</button>
            <button>Asema 2</button>
          </div>
        </div>
      </div>
    );
  }
}

export default Searchbar;
