import React, { Component } from "react";

class Searchbar extends Component {
  constructor(props) {
    super(props);
    this.handleDropdownVisible = this.handleDropdownVisible.bind(this);
    this.handleDropdownHidden = this.handleDropdownHidden.bind(this);
    this.state = { visible: false };
  }

  changeVisible() {
    this.setState({ visible: true });
  }
  changeHidden() {
    this.setState({ visible: false });
  }

  render() {
    return (
      <div id="search" className="card">
        <div className="card-header justify-content-center ">
          Valitse haluamasi mittausasema havaintohistoriaa varten
        </div>
        <div className="card-body justify-content-center">
          <input
            type="text"
            placeholder="Hae..."
            id="dropdowninput"
            onKeyUp=""
            onFocus={() => this.handleDropdownVisible()}
            onBlur={() => this.handleDropdownHidden()}
          />
          <div className="dropdown">
            <div
              id="dropdownbox"
              className={
                this.state.visible === false
                  ? "dropdown-content hidden"
                  : "dropdownc-content"
              }
            >
              <a href="">Valinta 1</a>
              <a href="">Valinta 2</a>
              <a href="">Valinta 3</a>
              <a href="">Valinta 4</a>
              <a href="">Valinta 5</a>
              <a href="">Valinta 6</a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Searchbar;
